import React, { useState } from "react";
import {Button, Drawer, Row, Col} from 'antd';

const settings = {
  position: 'absolute',
  top: '50%',
  right: '5px',
};

const AntSetting = () => {
  const [visible, setVisible] = useState(false);

  function onClose() {
    setVisible(false);
  }

  function onOpen() {
    setVisible(true);
  }

  return (
    <div>
      <Button type="primary"
              shape="round"
              icon="setting"
              style={settings}
              onClick={onOpen}
      />
      <Drawer
        title="Layout Settings"
        placement="right"
        closable={false}
        onClose={onClose}
        visible={visible}
      >
        <Row gutter={[{ xs: 8, sm: 16, md: 24, lg: 32 }, 20]}>
          <Col className="gutter-row" span={6}>
            <div className="gutter-box">col-6</div>
          </Col>
          <Col className="gutter-row" span={6}>
            <div className="gutter-box">col-6</div>
          </Col>
        </Row>
      </Drawer>
    </div>
  );
};

export default AntSetting;
