import React, { useState } from "react";
import {Button, Drawer, Row, Col} from 'antd';

const settings = {
  position: 'absolute',
  top: '50%',
  right: '5px',
};

const AntSetting = () => {
  const [visible, setVisible] = useState(false);

  function onClose() {
    setVisible(false);
  }

  function onOpen() {
    setVisible(true);
  }

  return (
    <div>
      <Button type="primary"
              shape="round"
              icon="setting"
              style={settings}
              onClick={onOpen}
      />
      <Drawer
        title="Layout Settings"
        placement="right"
        closable={false}
        onClose={onClose}
        visible={visible}
        width={512}
      >
        <Row gutter={16}>
          <Col className="gutter-row" span={12}>
            <div className="gutter-box">Primary Color</div>
          </Col>
        </Row>
      </Drawer>
    </div>
  );
};

export default AntSetting;
